package Exercise;

public class TestIncrement {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int  i=25;//Declaration of variable and value of i
		int iInc;//Declaration of variable of iInc
		iInc=i++;//assign value to iInc
		System.out.println("the value of iInc for i++= "+iInc);//print out the value of iInc
		System.out.println("the value of i= "+i);//print out the value of i
		iInc=++i;//assign new value to iInc
		System.out.println("the new value of iInc for ++i= "+iInc);//print out the value of new iInc
	}
}
